import React from 'react';

export default class User extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      userID:"",
      userName:""
    }
    this.myRef = React.createRef();
  }


  componentDidMount() {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => console.log(data.message));
    // fetch("/api/twitter/bts_bighit")
    //   .then((res) => res.json())
    //   .then((data) => {console.log(data.body.data.id)
    //    this.setState({userID:data.body.data.id})});
  }

  handleClick=()=>{
    fetch(`/api/tweets/${this.state.userID}`)
    .then((res) => res.json())
    .then((data) => console.log(data));
  }

  handleSearchUser=()=>{
    fetch(`/api/twitter/${this.state.userName}`)
    .then((res) => res.json())
    .then((data) => {console.log(data.body.data.id)
     this.setState({userID:data.body.data.id})});
  }

  onInputchange=(event)=> {
    this.setState({
      userName: event.target.value
    });
  }

  render() {
    return (
    //   <ul>
    //     { this.state.persons.map(person => <li>{person.name}</li>)}
    //   </ul>
          <div className="todo-form">
            <input
              placeholder='Enter Username'
              name="userName"
              type='text'
              value={this.state.userName}
              onChange={(event)=>this.onInputchange(event)}
              className='todo-input'
              ref={this.myRef}
            />
            <button onClick={this.handleSearchUser} className='todo-button'>
              Search User
            </button>
          </div>
    //<button onClick={this.handleClick}>Hello</button>
    )
  }
}
