import React from 'react';

export default class User extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      userID:"",
      userName:"",
      tweets:[],
      userError:""
    }
  }

  handleSearchUser=()=>{
    this.setState({
      userID:"",
      tweets:[],
      userError:""
    })
    fetch(`/api/twitter/${this.state.userName}`)
    .then((res) => res.json())
    .then((data) => 
    { 
      if(data.body.data.id)
      {
        this.setState({userID:data.body.data.id});
        fetch(`/api/tweets/${this.state.userID}`)
        .then((res) => res.json())
        .then((data) => {console.log(data.body.data)
        this.setState({tweets:data.body.data})
        });
      }
      if(data.body.errors){
        this.setState({userError:"Oops! User does not exist....."})
      }
    })
    .catch((error)=>{console.log(error,"popopop")
    this.setState({userError:"Oops! User does not exist....."})
  });
  }

  onInputchange=(event)=> {
    this.setState({
      userName: event.target.value
    });
  }

  render() {
    return (
    <>
          <div className="todo-form">
            <input
              placeholder='Enter Username'
              name="userName"
              type='text'
              value={this.state.userName}
              onChange={(event)=>this.onInputchange(event)}
              className='todo-input'
            />
            <button onClick={this.handleSearchUser} className='todo-button'>
              Search User
            </button>
          </div>
          {this.state.userID!==""?
            this.state.tweets?this.state.tweets.map(tweet=><div className="todo-row" key={tweet.id}>{tweet.text}</div>):<div>There are no Tweets</div>:
            <div>{this.state.userError}</div>
          }
    </>
    )
  }
}
