import React from 'react';

export default class User extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      userID:"",
      userName:"",
      tweets:[],
      userError:""
    }
  }


  componentDidMount() {
    // fetch("/api")
    //   .then((res) => res.json())
    //   .then((data) => console.log(data.message));
    // fetch("/api/twitter/bts_bighit")
    //   .then((res) => res.json())
    //   .then((data) => {console.log(data.body.data.id)
    //    this.setState({userID:data.body.data.id})});
  }

  handleClick=()=>{
    fetch(`/api/tweets/${this.state.userID}`)
    .then((res) => res.json())
    .then((data) => console.log(data));
  }

  handleSearchUser=()=>{
    fetch(`/api/twitter/${this.state.userName}`)
    .then((res) => res.json())
    .then((data) => 
    { 
      console.log(data.body)
      if(data.body.data.id)
      {
        this.setState({userID:data.body.data.id});
        fetch(`/api/tweets/${this.state.userID}`)
        .then((res) => res.json())
        .then((data) => {console.log(data.body.data)
        this.setState({tweets:data.body.data})
        });
      }
      else{
        this.setState({userError:"Oops! User does not exist....."})
      }
    });
  }

  onInputchange=(event)=> {
    this.setState({
      userName: event.target.value
    });
  }

  render() {
    return (
    //   <ul>
    //     { this.state.persons.map(person => <li>{person.name}</li>)}
    //   </ul>
    <>
          <div className="todo-form">
            <input
              placeholder='Enter Username'
              name="userName"
              type='text'
              value={this.state.userName}
              onChange={(event)=>this.onInputchange(event)}
              className='todo-input'
            />
            <button onClick={this.handleSearchUser} className='todo-button'>
              Search User
            </button>
          </div>
            {this.state.tweets?this.state.tweets.map(tweet=><div className="todo-row" key={tweet.id}>{tweet.text}</div>):<div className="todo-row">There are no Tweets</div>}
          </>
    //<button onClick={this.handleClick}>Hello</button>
    )
  }
}
